real[] yticks={3,2,1,-1,-2,-3,-4,-5,-6,-7};
draw(A--B--C--cycle);
dot(dots);
label("A(-3,1)",A,N);
label("B(7,1)",B,NE);
label("C(5,-3)",C,S);
label("D(5,1)",D,N);
xaxis(-5,9,Ticks(" ", xticks, 3),Arrows(4));
