draw(O--4*I, Arrow3(6));
draw(O--4*J, Arrow3(6));
draw(O--4*K, Arrow3(6));
draw(O--P);
draw(O--Q);
draw(O--R);
draw(P--Q--R--S,dashed);

label("$x$", 4.5*I);
label("$y$", 4.5*J);
label("$z$", 4.5*K);

dot("$P = (1,2,3)$", P, N);
dot("$Q = (1,2,-3)$", Q, SE);
dot("$R = (1,-2,3)$", R, NW);
